import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-task-filter',
  templateUrl: './task-filter.component.html',
  styleUrls: ['./task-filter.component.scss']
})
export class TaskFilterComponent {

  //  receitinha de bolo: "new EventEmitter()"
  @Output() onChangeFilter = new EventEmitter();

  filter:string = 'all'

  selectFilter(){
    console.log(this.filter)
  }

}

<div>
    <label for="filterTask" class="fw-bold">Filtrar</label>
    <select name="filterTask" id="filterTask" class="d-block mt-1 mb-3" [(ngModel)]="filter" (change)="selectFilter()" >
        <option value="all">Mostrar tudo</option>
        <option value="toDO">ToDo</option>
        <option value="doing">Doing</option>
        <option value="done">Done</option>
    </select>
</div>
